import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Building, MessageCircle, CheckCircle, XCircle, Clock, Settings, Trash2, RefreshCw } from 'lucide-react';
import QRModal from './QRModal';

const Dashboard = () => {
  const [connections, setConnections] = useState({
    ghl: null,
    whatsapp: null
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [showQRModal, setShowQRModal] = useState(false);
  const [qrCode, setQrCode] = useState('');
  const [whatsappAccountId, setWhatsappAccountId] = useState('');
  const [connectionLoading, setConnectionLoading] = useState({
    ghl: false,
    whatsapp: false
  });

  useEffect(() => {
    fetchConnections();
  }, []);

  const fetchConnections = async () => {
    try {
      const response = await axios.get('/api/connections');
      setConnections(response.data);
    } catch (error) {
      setError('Failed to fetch connections');
    } finally {
      setLoading(false);
    }
  };

  const connectGHL = async () => {
    setConnectionLoading({ ...connectionLoading, ghl: true });
    try {
      const response = await axios.get('/api/auth/ghl');
      window.open(response.data.authUrl, '_blank', 'width=600,height=700');

      // Poll for connection status
      const pollInterval = setInterval(async () => {
        try {
          const connectionResponse = await axios.get('/api/connections');
          if (connectionResponse.data.ghl) {
            setConnections(connectionResponse.data);
            setSuccess('GoHighLevel connected successfully!');
            clearInterval(pollInterval);
            setConnectionLoading({ ...connectionLoading, ghl: false });
          }
        } catch (error) {
          console.error('Polling error:', error);
        }
      }, 3000);

      // Stop polling after 5 minutes
      setTimeout(() => {
        clearInterval(pollInterval);
        setConnectionLoading({ ...connectionLoading, ghl: false });
      }, 300000);

    } catch (error) {
      setError('Failed to initiate GHL connection');
      setConnectionLoading({ ...connectionLoading, ghl: false });
    }
  };

  const connectWhatsApp = async () => {
    setConnectionLoading({ ...connectionLoading, whatsapp: true });
    try {
      const response = await axios.post('/api/whatsapp/connect');
      setQrCode(response.data.qrCode);
      setWhatsappAccountId(response.data.accountId);
      setShowQRModal(true);
    } catch (error) {
      setError('Failed to generate WhatsApp QR code');
      setConnectionLoading({ ...connectionLoading, whatsapp: false });
    }
  };

  const disconnectService = async (service) => {
    try {
      await axios.delete(`/api/connections/${service}`);
      setConnections({
        ...connections,
        [service]: null
      });
      setSuccess(`${service === 'ghl' ? 'GoHighLevel' : 'WhatsApp'} disconnected successfully!`);
    } catch (error) {
      setError(`Failed to disconnect ${service}`);
    }
  };

  const onQRModalClose = () => {
    setShowQRModal(false);
    setQrCode('');
    setWhatsappAccountId('');
    setConnectionLoading({ ...connectionLoading, whatsapp: false });
  };

  const onWhatsAppConnected = () => {
    setShowQRModal(false);
    setSuccess('WhatsApp connected successfully!');
    setConnectionLoading({ ...connectionLoading, whatsapp: false });
    fetchConnections();
  };

  const ConnectionCard = ({ 
    title, 
    description, 
    icon: Icon, 
    connected, 
    onConnect, 
    onDisconnect, 
    loading, 
    disabled = false,
    connectionData 
  }) => (
    <div className="card">
      <div className="flex items-start justify-between">
        <div className="flex items-center space-x-3">
          <div className={`p-3 rounded-lg ${connected ? 'bg-green-100' : 'bg-gray-100'}`}>
            <Icon className={`w-6 h-6 ${connected ? 'text-green-600' : 'text-gray-600'}`} />
          </div>
          <div>
            <h3 className="text-lg font-semibold text-gray-900">{title}</h3>
            <p className="text-sm text-gray-600">{description}</p>
          </div>
        </div>

        <div className="flex items-center space-x-2">
          {connected ? (
            <div className="flex items-center space-x-2">
              <div className="status-connected">
                <CheckCircle className="w-4 h-4 mr-1" />
                <span className="text-sm font-medium">Connected</span>
              </div>
              <button
                onClick={onDisconnect}
                className="p-2 text-red-600 hover:bg-red-50 rounded-lg transition-colors"
                title="Disconnect"
              >
                <Trash2 className="w-4 h-4" />
              </button>
            </div>
          ) : (
            <button
              onClick={onConnect}
              disabled={disabled || loading}
              className={`px-4 py-2 rounded-lg font-medium transition-colors ${
                disabled
                  ? 'bg-gray-200 text-gray-500 cursor-not-allowed'
                  : loading
                  ? 'bg-blue-400 text-white cursor-wait'
                  : 'bg-blue-600 text-white hover:bg-blue-700'
              }`}
            >
              {loading ? (
                <div className="flex items-center">
                  <RefreshCw className="w-4 h-4 mr-2 animate-spin" />
                  Connecting...
                </div>
              ) : (
                'Connect'
              )}
            </button>
          )}
        </div>
      </div>

      {connected && connectionData && (
        <div className="mt-4 pt-4 border-t border-gray-200">
          <div className="grid grid-cols-2 gap-4 text-sm">
            {connectionData.locationId && (
              <div>
                <span className="font-medium text-gray-700">Location ID:</span>
                <p className="text-gray-600 truncate">{connectionData.locationId}</p>
              </div>
            )}
            {connectionData.accountId && (
              <div>
                <span className="font-medium text-gray-700">Account ID:</span>
                <p className="text-gray-600 truncate">{connectionData.accountId}</p>
              </div>
            )}
            {connectionData.createdAt && (
              <div>
                <span className="font-medium text-gray-700">Connected:</span>
                <p className="text-gray-600">{new Date(connectionData.createdAt).toLocaleDateString()}</p>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="loading-spinner h-12 w-12"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-6xl mx-auto px-4 py-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Integration Dashboard</h1>
          <p className="text-gray-600">
            Connect your WhatsApp and GoHighLevel accounts to start syncing messages
          </p>
        </div>

        {/* Alerts */}
        {error && (
          <div className="alert alert-error mb-6 fade-in">
            {error}
            <button
              onClick={() => setError('')}
              className="ml-auto text-red-600 hover:text-red-800"
            >
              ×
            </button>
          </div>
        )}

        {success && (
          <div className="alert alert-success mb-6 fade-in">
            {success}
            <button
              onClick={() => setSuccess('')}
              className="ml-auto text-green-600 hover:text-green-800"
            >
              ×
            </button>
          </div>
        )}

        {/* Connection Status Overview */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
          <div className="card">
            <div className="flex items-center space-x-3">
              <div className={`p-3 rounded-lg ${connections.ghl ? 'bg-green-100' : 'bg-gray-100'}`}>
                <Building className={`w-6 h-6 ${connections.ghl ? 'text-green-600' : 'text-gray-600'}`} />
              </div>
              <div>
                <h3 className="font-semibold text-gray-900">GoHighLevel</h3>
                <p className={`text-sm ${connections.ghl ? 'text-green-600' : 'text-gray-500'}`}>
                  {connections.ghl ? 'Connected' : 'Not Connected'}
                </p>
              </div>
            </div>
          </div>

          <div className="card">
            <div className="flex items-center space-x-3">
              <div className={`p-3 rounded-lg ${connections.whatsapp ? 'bg-green-100' : 'bg-gray-100'}`}>
                <MessageCircle className={`w-6 h-6 ${connections.whatsapp ? 'text-green-600' : 'text-gray-600'}`} />
              </div>
              <div>
                <h3 className="font-semibold text-gray-900">WhatsApp</h3>
                <p className={`text-sm ${connections.whatsapp ? 'text-green-600' : 'text-gray-500'}`}>
                  {connections.whatsapp ? 'Connected' : 'Not Connected'}
                </p>
              </div>
            </div>
          </div>

          <div className="card">
            <div className="flex items-center space-x-3">
              <div className={`p-3 rounded-lg ${connections.ghl && connections.whatsapp ? 'bg-green-100' : 'bg-gray-100'}`}>
                <Settings className={`w-6 h-6 ${connections.ghl && connections.whatsapp ? 'text-green-600' : 'text-gray-600'}`} />
              </div>
              <div>
                <h3 className="font-semibold text-gray-900">Integration</h3>
                <p className={`text-sm ${connections.ghl && connections.whatsapp ? 'text-green-600' : 'text-gray-500'}`}>
                  {connections.ghl && connections.whatsapp ? 'Active' : 'Inactive'}
                </p>
              </div>
            </div>
          </div>
        </div>

        {/* Connection Setup */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          <ConnectionCard
            title="GoHighLevel"
            description="Connect your GHL account to sync conversations"
            icon={Building}
            connected={!!connections.ghl}
            onConnect={connectGHL}
            onDisconnect={() => disconnectService('ghl')}
            loading={connectionLoading.ghl}
            connectionData={connections.ghl}
          />

          <ConnectionCard
            title="WhatsApp"
            description="Connect WhatsApp via QR code scan"
            icon={MessageCircle}
            connected={!!connections.whatsapp}
            onConnect={connectWhatsApp}
            onDisconnect={() => disconnectService('whatsapp')}
            loading={connectionLoading.whatsapp}
            disabled={!connections.ghl}
            connectionData={connections.whatsapp}
          />
        </div>

        {/* Integration Instructions */}
        {!connections.ghl && (
          <div className="mt-8 card border-l-4 border-blue-500">
            <h3 className="text-lg font-semibold text-gray-900 mb-2">Getting Started</h3>
            <ol className="list-decimal list-inside space-y-2 text-gray-600">
              <li>First, connect your GoHighLevel account using OAuth</li>
              <li>Once GHL is connected, you can connect WhatsApp using QR code</li>
              <li>After both connections are active, messages will sync automatically</li>
            </ol>
          </div>
        )}

        {connections.ghl && !connections.whatsapp && (
          <div className="mt-8 card border-l-4 border-green-500">
            <h3 className="text-lg font-semibold text-gray-900 mb-2">Next Step</h3>
            <p className="text-gray-600">
              Great! GoHighLevel is connected. Now connect your WhatsApp account to start syncing messages.
            </p>
          </div>
        )}

        {connections.ghl && connections.whatsapp && (
          <div className="mt-8 card border-l-4 border-green-500">
            <h3 className="text-lg font-semibold text-gray-900 mb-2">Integration Active</h3>
            <p className="text-gray-600 mb-4">
              Perfect! Both accounts are connected. Messages are now syncing between WhatsApp and GoHighLevel.
            </p>
            <div className="bg-blue-50 p-4 rounded-lg">
              <h4 className="font-medium text-blue-900 mb-2">How it works:</h4>
              <ul className="list-disc list-inside space-y-1 text-blue-800 text-sm">
                <li>Incoming WhatsApp messages appear in your GHL conversations</li>
                <li>Replies from GHL are sent back to WhatsApp</li>
                <li>All message history is preserved in both platforms</li>
              </ul>
            </div>
          </div>
        )}
      </div>

      {/* QR Modal */}
      {showQRModal && (
        <QRModal
          qrCode={qrCode}
          accountId={whatsappAccountId}
          onClose={onQRModalClose}
          onConnected={onWhatsAppConnected}
        />
      )}
    </div>
  );
};

export default Dashboard;
